wget -q -O - https://www.cbr.ru/currency_base/daily.aspx?date_req=19.11.2016 | tee Bank | grep -o -E '<td>[0-9]{2},[0-9]*?<\/td>' | sed -e 's/<td>//' -e 's/<\/td>//' > Bank1 ;  grep -o -E '<td>[A-Z]{3}</td>' Bank | sed -e 's/<td>//' -e 's/<\/td>//' | paste --delimiters=- - Bank1 > Course; rm Bank Bank1
